import { BN } from "@switchboard-xyz/common"; // eslint-disable-line @typescript-eslint/no-unused-vars
export class Coin {
    value;
    constructor(fields) {
        this.value = fields.value;
    }
    toJSON() {
        return {
            value: this.value.toString(),
        };
    }
    static fromJSON(obj) {
        return new Coin({
            value: new BN(obj.value),
        });
    }
    toMoveStruct() {
        return {
            value: this.value.toString(),
        };
    }
    static fromMoveStruct(obj) {
        return new Coin({
            value: new BN(obj.value),
        });
    }
}
